$.fn.poligonal=function(b,a){return this.each(function(){var v=this.getContext("2d");if(b>1&&a>1){var s=30;var q=2*Math.PI/b;var f=(a-1)*s;var c=f/(2*Math.sin(q/2));var g=Math.cos(q/2)*f;var h=c+10;var d=c+10;for(i=2;i<=a;i++){f=(i-1)*s;c=f/(2*Math.sin(q/2));g=Math.cos(q/2)*f;v.beginPath();v.arc(h,d,c,0,360,false);v.stroke();for(j=0;j<b;j++){var u=q*(j-0.5);var m=c*Math.cos(u);var e=c*Math.sin(u);var t=c*Math.cos(u+q);var o=c*Math.sin(u+q);v.beginPath();v.moveTo(h+m,d+e);v.lineTo(h+t,d+o);v.stroke();for(k=0;k<a;k++){var p=m+(((t+1-m)/(i-1))*k);var n=e+(((o+1-e)/(i-1))*k);v.beginPath();v.arc(h+p,d+n,s*0.6/2,2*Math.PI,false);v.fill()}}}}})};